// Импорты React и необходимых компонентов и стилей
import React, { useState, useEffect } from "react"
import Header from "./Header"
import Main from "./Main"
import Footer from "./Footer"
import ImagePopup from "./ImagePopup"
import apiConfig from "../utils/Api"
import { CurrentUserContext } from "../contexts/CurrentUserContext"
import EditProfilePopup from "./EditProfilePopup"
import EditAvatarPopup from "./EditAvatarPopup"
import AddPlacePopup from "./AddPlacePopup"
import Login from "./Login"
import Register from "./Register"
import { Routes, Route } from "react-router-dom"
import ProtectedRouteElement from "./ProtectedRoute"
import { Navigate } from "react-router-dom"
import InfoTooltip from "./InfoTooltip"
import successedImage from "../images/info-tooltip-success.svg"
import failImage from "../images/info-tooltip-error.svg"


// Основной компонент приложения
function App() {
  // Стейт для хранения состояния авторизации
  const [loggedIn, setLoggedIn] = useState(false)

  // Стейт для хранения массива карточек
  const [cards, setCards] = useState([])

  // Стейт для хранения данных о текущем пользователе
  const [currentUser, setCurrentUser] = useState({})

  // Стейты для управления видимостью попапов
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false)
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false)
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false)

  const [isInfoTooltipOpen, setIsInfoTooltipOpen] = useState(false)
  const [isInfoTooltipSuccessed, setIsInfoTooltipSuccessed] = useState(false)
  const [email, setEmail] = useState('yambikov@yandex.ru')

  // Стейт для отображения попапа с изображением
  const [selectedCard, setSelectedCard] = useState({}) // {name, link}

  // Загрузка карточек с сервера при монтировании компонента
  useEffect(() => {
    apiConfig
      .getInitialCards()
      .then((res) => {
        setCards(res)
      })
      .catch((err) => {
        console.log(err)
      })
  }, [])

  // Загрузка данных о текущем пользователе с сервера
  useEffect(() => {
    apiConfig
      .getUserInfoApi()
      .then((res) => {
        setCurrentUser(res)
      })
      .catch((err) => {
        console.log(err)
      })
  }, [])

  useEffect(() => {
    handleTokenCheck()
  }, [])

  // Проверка токена
  const handleTokenCheck = () => {
    if (localStorage.getItem('token')){
      const token = localStorage.getItem('jwt');
      auth.checkToken(token)
      .then((res) => {
        // let calGoal = 0;
        // // найдём выбранное пользователем количество калорий
        // // из списка возможных целей
        // calData.calData.forEach((goal) => {
        //   if (goal.id === res.ru_cal_goal){
        //     // цель, выбранная пользователем
        //     calGoal = goal.calGoal;
        //   }
        // })
        if (res){
          setLoggedIn(true);
          navigate("/", {replace: true})
        }
      });
    }
  }

  // Обработчик лайка карточки
  function handleCardLike(card) {
    // Проверяем, есть ли уже лайк на этой карточке
    const isLiked = card.likes.some((i) => i._id === currentUser._id)

      // Отправляем запрос в API для лайка или дизлайка в зависимости от наличия лайка
      ; (isLiked ? apiConfig.deleteLike(card._id) : apiConfig.putLike(card._id))
        .then((res) => {
          setCards((state) => state.map((c) => (c._id === card._id ? res : c)))
        })
        .catch((err) => {
          console.log(err)
        })
  }

  // Обработчик удаления карточки
  function handleCardDelete(card) {
    apiConfig
      .deleteCard(card._id)
      .then(() => {
        setCards((state) =>
          state.filter((c) => (c._id === card._id ? false : true))
        )
      })
      .catch((err) => {
        console.log(err)
      })
  }

  // Обработчики кликов по кнопкам попапов и карточкам
  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true)
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true)
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true)
  }

  function handleCardClick(selectedCard) {
    setSelectedCard(selectedCard)
  }

  // Закрытие всех попапов
  function closeAllPopups() {
    setIsEditProfilePopupOpen(false)
    setIsAddPlacePopupOpen(false)
    setIsEditAvatarPopupOpen(false)
    setSelectedCard({})
    setIsInfoTooltipOpen(false)
  }

  // Обновление данных пользователя
  function handleUpdateUser(data) {
    apiConfig
      .patchUserInfo(data)
      .then((res) => {
        setCurrentUser(res)
      })
      .then(closeAllPopups)
      .catch((err) => {
        console.log(err)
      })
  }

  // Обновление аватара пользователя
  function handleUpdateAvatar(data) {
    apiConfig
      .patchAvatar(data)
      .then((res) => {
        setCurrentUser(res)
      })
      .then(closeAllPopups)
      .catch((err) => {
        console.log(err)
      })
  }

  // Добавление новой карточки
  function handleAddPlaceSubmit(data) {
    apiConfig
      .postCard(data)
      .then((res) => {
        setCards([res, ...cards])
      })
      .then(closeAllPopups)
      .catch((err) => {
        console.log(err)
      })
  }

  function signOut() {
    setLoggedIn(false)
    console.log('setLoggedIn');
  }

  const handleLogin = () => {
    setLoggedIn(true);
  }

  // Рендеринг компонента
  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="root brd!">
        <div className="page">
          <Header email={email} signOut={signOut} />
          {/* <Route path="/" element={loggedIn ? <Navigate to="/main" replace /> : <Navigate to="/sign-in" replace />} /> */}
          {/* <Route path="/sign-up" element={<ProtectedRouteElement element={Register} loggedIn={loggedIn} />} /> */}
          <Routes>
            <Route path="/sign-up" element={<Login handleLogin={handleLogin} />} />
            <Route path="/sign-in" element={<Register />} />
            <Route
              path="/"
              element={
                <ProtectedRouteElement
                  element={Main} // Рендерим Main, если пользователь авторизован
                  loggedIn={loggedIn}
                  onEditProfile={handleEditProfileClick}
                  onAddPlace={handleAddPlaceClick}
                  onEditAvatar={handleEditAvatarClick}
                  onCardClick={handleCardClick}
                  onCardLike={handleCardLike}
                  onCardDelete={handleCardDelete}
                  cards={cards}
                />
              }
            />
          </Routes>
          <Footer />
          <EditProfilePopup
            isOpen={isEditProfilePopupOpen}
            onClose={closeAllPopups}
            onUpdateUser={handleUpdateUser}
          />
          <EditAvatarPopup
            isOpen={isEditAvatarPopupOpen}
            onClose={closeAllPopups}
            onUpdateAvatar={handleUpdateAvatar}
          />
          <AddPlacePopup
            isOpen={isAddPlacePopupOpen}
            onClose={closeAllPopups}
            onUpdateCard={handleAddPlaceSubmit}
          />
          <ImagePopup card={selectedCard} onClose={closeAllPopups} />
          <InfoTooltip
            isOpen={isInfoTooltipOpen}
            onClose={closeAllPopups}
            image={isInfoTooltipSuccessed ? successedImage : failImage}
            alt={
              isInfoTooltipSuccessed
                ? "Успешная регистрация"
                : "Ошибка регистрации"
            }
            message={
              isInfoTooltipSuccessed
                ? "Вы успешно зарегистрировались!"
                : "Что-то пошло не так! Попробуйте ещё раз."
            }
          />
        </div>
      </div>
    </CurrentUserContext.Provider>
  )
}

export default App
