// Импорты React и необходимых компонентов и стилей
import React, { useState, useEffect } from "react";
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import ImagePopup from "./ImagePopup";
import apiConfig from "../utils/Api";
import { CurrentUserContext } from "../contexts/CurrentUserContext";
import EditProfilePopup from "./EditProfilePopup";
import EditAvatarPopup from "./EditAvatarPopup";
import AddPlacePopup from "./AddPlacePopup";
import SingIn from "./SignIn"; 
import SingUp from "./SignUp";


// Основной компонент приложения
function App() {
  // Стейт для хранения массива карточек
  const [cards, setCards] = useState([]);

  // Стейт для хранения данных о текущем пользователе
  const [currentUser, setCurrentUser] = useState({});

  // Стейты для управления видимостью попапов
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);

  // Стейт для отображения попапа с изображением
  const [selectedCard, setSelectedCard] = useState({}); // {name, link}

  // Загрузка карточек с сервера при монтировании компонента
  useEffect(() => {
    apiConfig
      .getInitialCards()
      .then((res) => {
        setCards(res);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  // Загрузка данных о текущем пользователе с сервера
  useEffect(() => {
    apiConfig
      .getUserInfoApi()
      .then((res) => {
        setCurrentUser(res);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  // Обработчик лайка карточки
  function handleCardLike(card) {
    // Проверяем, есть ли уже лайк на этой карточке
    const isLiked = card.likes.some((i) => i._id === currentUser._id);

    // Отправляем запрос в API для лайка или дизлайка в зависимости от наличия лайка
    (isLiked ? apiConfig.deleteLike(card._id) : apiConfig.putLike(card._id))
      .then((res) => {
        setCards((state) => state.map((c) => (c._id === card._id ? res : c)));
      })
      .catch((err) => {
        console.log(err);
      });
  }

  // Обработчик удаления карточки
  function handleCardDelete(card) {
    apiConfig
      .deleteCard(card._id)
      .then(() => {
        setCards((state) => state.filter((c) => (c._id === card._id ? false : true)));
      })
      .catch((err) => {
        console.log(err);
      });
  }

  // Обработчики кликов по кнопкам попапов и карточкам
  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }

  function handleCardClick(selectedCard) {
    setSelectedCard(selectedCard);
  }

  // Закрытие всех попапов
  function closeAllPopups() {
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setSelectedCard({});
  }

  // Обновление данных пользователя
  function handleUpdateUser(data) {
    apiConfig
      .patchUserInfo(data)
      .then((res) => {
        setCurrentUser(res);
      })
      .then(closeAllPopups)
      .catch((err) => {
        console.log(err);
      });
  }

  // Обновление аватара пользователя
  function handleUpdateAvatar(data) {
    apiConfig
      .patchAvatar(data)
      .then((res) => {
        setCurrentUser(res);
      })
      .then(closeAllPopups)
      .catch((err) => {
        console.log(err);
      })
  }

  // Добавление новой карточки
  function handleAddPlaceSubmit(data) {
    apiConfig
      .postCard(data)
      .then((res) => {
        setCards([res, ...cards]);
      })
      .then(closeAllPopups)
      .catch((err) => {
        console.log(err);
      });
  }

  // Рендеринг компонента
  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="root">
        <div className="page">
          <Header />
          <SingIn />
          <SingU />
          {/* <Main
            onEditProfile={handleEditProfileClick}
            onAddPlace={handleAddPlaceClick}
            onEditAvatar={handleEditAvatarClick}
            onCardClick={handleCardClick}
            onCardLike={handleCardLike}
            onCardDelete={handleCardDelete}
            cards={cards}
          />
          <Footer />
          <EditProfilePopup
            isOpen={isEditProfilePopupOpen}
            onClose={closeAllPopups}
            onUpdateUser={handleUpdateUser}
          />
          <EditAvatarPopup
            isOpen={isEditAvatarPopupOpen}
            onClose={closeAllPopups}
            onUpdateAvatar={handleUpdateAvatar}
          />
          <AddPlacePopup
            isOpen={isAddPlacePopupOpen}
            onClose={closeAllPopups}
            onUpdateCard={handleAddPlaceSubmit}
          /> */}
          <ImagePopup card={selectedCard} onClose={closeAllPopups} />
        </div>
      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
