import React from 'react';
import { useForm } from 'react-hook-form';
import { Link } from 'react-router-dom';

function Register() {
  const { register, handleSubmit, errors, formState } = useForm({
    mode: 'onChange' // Проверка на каждое изменение поля
  });

  const onSubmit = (data) => {
    console.log(data);
  };

  return (
    <main>
      <section className="auth">
        <form
          className="auth__form"
          name="registration"
          onSubmit={handleSubmit(onSubmit)}
        >
          <h2 className="auth__title">Регистрация</h2>
          <input
            className="auth__item"
            name="email"
            type="email"
            placeholder="Email"
            autoComplete="off"
            ref={register({
              required: 'Email обязателен',
              pattern: {
                value: /^[^\s@]+@[^\s@]+\.[^\s@]+$/,
                message: 'Неверный формат email',
              },
            })}
          />
          {errors.email && (
            <span className="input-error-name error">{errors.email.message}</span>
          )}

          <input
            className="auth__item"
            name="password"
            type="password"
            placeholder="Пароль"
            autoComplete="off"
            ref={register({
              required: 'Пароль обязателен',
              minLength: {
                value: 6,
                message: 'Пароль должен содержать минимум 6 символов',
              },
            })}
          />
          {errors.password && (
            <span className="input-error-link error">{errors.password.message}</span>
          )}

          <button
            className={`auth__button ${formState.isValid ? "auth__button_type_active" : ""}`}
            type="submit"
            disabled={!formState.isValid}
          >
            Зарегистрироваться
          </button>
          <p className="auth__text">
            Уже зарегистрированы?
            <Link className="auth__login-link" to="/sign-up">
              Войти
            </Link>
          </p>
        </form>
      </section>
    </main>
  );
}

export default Register;
