import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import * as auth from '../utils/auth';

function Register() {
  const [formValue, setFormValue] = useState({
    email: '',
    password: '',
  });

  const [isFormValid, setIsFormValid] = useState(false);

  function handleSubmit(e) {
    e.preventDefault();

      auth.register(formValue.username,  formValue.email).then((res) => {
        navigate('/login', {replace: true});
        }
      );


  }

  const handleChange = (e) => {
    const { name, value } = e.target;

    setFormValue({
      ...formValue,
      [name]: value,
    });

    validateForm();
  };

  // Функция валидации для проверки, является ли форма валидной
  const validateForm = () => {
    const { email, password } = formValue;

    const isEmailValid = /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
    const isPasswordValid = password.length >= 6;

    setIsFormValid(isEmailValid && isPasswordValid);
  };

  return (
    <main>
      <section className="auth">
        <form
          className="auth__form"
          name="registration"
          onSubmit={handleSubmit}
        >
          <h2 className="auth__title">Регистрация</h2>
          <input
            className="auth__item"
            name="email"
            type="email"
            placeholder="Email"
            minLength={2}
            maxLength={30}
            autoComplete="off"
            required
            value={formValue.email}
            onChange={handleChange}
          />
          <span className="input-error-name error" />
          <input
            className="auth__item"
            name="password"
            type="password"
            placeholder="Пароль"
            required
            autoComplete="off"
            value={formValue.password}
            onChange={handleChange}
          />
          <span className="input-error-link error" />

          <button
            className={`auth__button ${isFormValid ? "auth__button_type_active" : ""}`}
            type="submit"
            disabled={!isFormValid}
          >
            Зарегистрироваться
          </button>
          <p className="auth__text">
            Уже зарегистрированы?
            <Link className="auth__login-link" to="/sign-up">
              Войти
            </Link>
          </p>
        </form>
      </section>
    </main>
  );
}

export default Register;
